function scrollToTarget(selector=""){selector&&document.querySelector(selector)&&document.querySelector(selector).scrollIntoView({behavior:"smooth"})}window.addEventListener("mousemove",e=>{const elem=document.createElement("div"),spread=Math.round(8*Math.random())+3,blur=spread*(Math.round(12*Math.random())+2);elem.classList.add("absolute","bg-light-green","inline-block","rounded-full","play-dot"),elem.style=`top: ${e.pageY}; left: ${e.pageX}; box-shadow: 0 0 ${blur}px ${spread}px rgba(57, 240, 185, .9); filter: hue-rotate(${180*Math.random()}deg) brightness(${1*Math.random()+.3});`,document.getElementById("bg-play").append(elem),setTimeout((function(){elem.remove()}),1e3)});